import {useEffect} from "react"
import {style} from './style'

const Orders = ({Order}) => {

    return (
        <main>
            <section className='container'>
                <h1>Ստացված պատվերներ</h1>
            {[...Order].reverse().map(item => (
                <section key={item.id} className="card">
                    <h2> {item.desc.name} </h2>
                    <ul>
                        <li><strong>Հեռ`</strong> {item.desc.phone}</li>
                        <li><strong>Էլ. հասցե`</strong> {item.desc.email}</li>
                        <li><strong>Առաջադրանք`</strong> {item.desc.task ? item.desc.task : 'Lրացված չէ'} </li>
                    </ul>
                </section>
            ))}
            </section>
            <style jsx>{style}</style>
        </main>
    )
}

Orders.getInitialProps = async () => {
    const response = await fetch('http://localhost:3000/api/order-form')
    const Order = await response.json()
    console.log(Order)
    return {Order}
}

export default Orders
